const express = require('express');
const fileUpload = require('express-fileupload');
const app = express();
const cors = require('cors');
const port = 5000;
const fs = require('fs');

app.use(express.json());
app.use(cors());
app.use(fileUpload());

app.get('/', (req, res) => {
  res.set({
    'Access-Control-Allow-Origin': '*'
  });
  res.send(JSON.stringify('CARD'));
});


const readData = () => {
  return fs.readFileSync('./data.json', (err, data) => {
    if (err) console.error(err, 'error in read');
    if (!data) return false;
    return data;
  });
};

const writeData = data => {
  return fs.writeFile('./data.json', data, err => {
    if (err) console.error(err, 'error in post');
    return data;
  });
}

app.get('/wines', async (req, res) => {
  const data = readData();
  const parsedData = JSON.parse(data);
  res.send(JSON.stringify(parsedData));
});

app.post('/wines', (req, res) => {
  const data = readData();
  const parsedData = JSON.parse(data);
  const wineArr = parsedData.wineArr;
  wineArr.push(req.body);
  const jsonWine = JSON.stringify(wineArr);
  writeData(jsonWine);
  res.send(jsonWine);
});

// {
